class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> nxt(n,0);
        for(int i=1;i<n;i++){
            nxt[i]=nxt[i-1]+1;
        }
        set<int> s;
        for(int i=0;i<n;i++) s.insert(i);
        vector<int> ans;
        for(auto i:queries){
            auto lb=s.lower_bound(i[0]+1);
            auto ub=s.lower_bound(i[1]);
            s.erase(lb,ub);
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
