class Solution {
public:

    void f(vector<int> &dis, vector<int> &ans, int n, vector<vector<int>>& q){
        set<int> s;
        for (int i = 0; i < n; ++i) {
            s.insert(i);
        }

        for (auto& x : q) {
            int l = x[0];
            int r = x[1];
            auto lb = s.lower_bound(l + 1);
            auto ub = s.lower_bound(r);
            s.erase(lb, ub);
            ans.push_back(s.size() - 1);
        }
    }

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> dis(n, 0);
        for (int i = 1; i < n; ++i) {
            dis[i] = dis[i - 1] + 1;
        }

        vector<int> ans;

        f(dis, ans, n, q);


        return ans;
    }
};
