class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> out;
        set<int> t;
        for(int i = 0; i < n;++i)t.insert(i);
        for(auto& p:queries){
            int u = p[0];
            int v = p[1];
            auto it = t.upper_bound(u);
            while(it != t.end() && *it < v){
                int val = *it++;
                t.erase(val);
            }
            out.push_back(t.size()-1);
        }
        return out;
    }
};
