class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int>s;
        vector<int>res;
        for(int i=0; i<n; i++)  s.insert(i);
        for(auto &it:queries){
            auto l = s.upper_bound(it[0]);
            if(*l==it[0])   l++;
            auto h = s.lower_bound(it[1]);
            if(*l <= *h)
            s.erase(l,h);
            res.push_back(s.size()-1);
        }
        return res;
    }
};
