class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> jumps(n);
        set<int> set;
        vector<int> ans;
        for (int i = 0; i < n; ++i) {
            jumps[i] = i;
            set.insert(i);
        }
        for (auto &query : queries) {
            int via1 = query[0];
            int via2 = query[1];
            auto left = set.lower_bound(via1 + 1);
            auto right = set.lower_bound(via2);
            set.erase(left, right);
            ans.push_back(set.size() - 1);
        }
        return ans;
    }
};
