class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i=0;i<n;i++){
            s.insert(i);
        }
        vector<int> ans;
        int d=n-1;
        //cout<<queries[0][0]<<" ";
        for(int i=0;i<queries.size();i++){
            //cout<<queries[i][0]<<" ";
            if (s.find(queries[i][0])==s.end() || s.find(queries[i][1])==s.end()){

            }else{
                auto it=next(s.find(queries[i][0]));
                //cout<<(*it)<<" ";
                while(*it!=queries[i][1]){
                    auto it2=it;
                    int tmp=*next(it2);
                    //cout<<(*it)<<" \n";
                    s.erase(it);
                    it=s.find(tmp);
                    d--;
                }
            }
            ans.push_back(d);
        }
        return ans;
    }
};
