class Solution:
    def shortestDistanceAfterQueries(self, n: int, qs: List[List[int]]) -> List[int]:
        from sortedcontainers import SortedList
        st = SortedList(list(range(n)))
        res = []
        for a, b in qs:
            p = st.bisect_right(a)
            while p < len(st) and  st[p] < b:
                st.pop(p)
                # p += 1
            # print(st)
            res.append(len(st) - 1)
        return res
