class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        unordered_map<int, int> mp;
        vector<int> ans;
        for(int i = 0; i < n - 1; i++) mp[i] = i + 1;
        for(auto &q: queries) {
            if (mp.count(q[0]) && mp[q[0]] < q[1]) {
                for(int i = mp[q[0]]; i < q[1]; i++) {
                    mp.erase(i);
                }
                mp[q[0]] = q[1];
            }
            ans.push_back(mp.size());
        }
        return ans;
    }
};
