from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        intervals = SortedList()
        for i in range(1, n):
            intervals.add((i - 1, i))
        res = []
        for beg, end in queries:
            beg_in = intervals.bisect_left((beg, 0))
            end_ex = intervals.bisect_left((end, 0))
            if end_ex == beg_in:
                res.append(len(intervals))
                continue
            for t in intervals[beg_in:end_ex]:
                intervals.remove(t)
            intervals.add((beg, end))
            res.append(len(intervals))
        return res
