class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        unordered_map<int,int> mp;
        for(int i=0; i<n-1; i++){
            mp[i]=i+1;
        }

        vector<int> ans;
        for(auto it:queries){
            int u = it[0];
            int v = it[1];
            bool tmp = 0;

            while(mp.find(u) != mp.end() && u<v){
                int x = mp[u];
                mp.erase(u);
                u = x;
                tmp = 1;
            }

            if(tmp) mp[it[0]] = it[1];
            ans.push_back(mp.size());
        }
        return ans;
    }
};
