class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i=0;i<n;i++) s.insert(i);
        vector<int> res;
        for(auto i:queries){
            int u=i[0],v=i[1];
            auto start=s.upper_bound(u);
            auto end=s.lower_bound(v);
            if(end==s.begin()){
                res.push_back(s.size()-1);
                continue;
            }
            if(start==s.end()){
                res.push_back(s.size()-1);
                continue;
            }
            end=prev(s.lower_bound(v));
            if(*end<*start){
                res.push_back(s.size()-1);
                continue;
            }
            int value=*end;
            while(*start<=value){
                auto val=next(start);
                s.erase(start);
                start=val;
            }
            res.push_back(s.size()-1);
        }
        return res;
    }
};
