class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        unordered_map<int, int> mp;
        int ans = n;
        vector<int> ret;
        int q = queries.size();
        for(int i = 0; i <= n; i++) mp[i] = i+1;
        for(int i = 0; i < q; i++){
            int s = queries[i][0], e = queries[i][1];
            if(mp.count(s) && mp.count(e)){
                for(int j = mp[s]; j < e;){
                    int next = mp[j];
                    if(mp.count(j)){
                        mp.erase(j);
                        ans--;
                    }
                    j = next;
                }
                mp[s] = e;
            }
            ret.push_back(ans-1);
        }
        return ret;
    }
};
