class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int cur = n-1;
        vector<int> ans;
        map<int, int> mp;
        for(int i=0;i<n-1;i++){
            mp[i] = i+1;
        }

        for(auto &q : queries){
            auto it = mp.upper_bound(q[0]);
            if(mp.empty()){
                mp[q[0]] = q[1];
            }
            else if(it == mp.end()){
                --it;
                if(it->second < q[1])
                    mp[q[0]] = q[1];
            }
            else if(it == mp.begin()){
                if(it->second < q[1]){
                    mp.erase(it);
                    mp[q[0]] = q[1];
                }
                else if(it->first >= q[1])
                    mp[q[0]] = q[1];
            }
            else {
                auto ir = it;
                --it;
                if(it->first <= q[0] && it->second >= q[1]){
                    ;
                }
                else if(ir->first >= q[1] && it->first <= q[0]){
                    mp[q[0]] = q[1];
                }
                else {   
                    auto start = it;
                    bool flag = false;
                    for(;it !=mp.end();it++){
                        if(it->first < q[0] || it->second > q[1]){
                            break;
                        }
                    }
                    mp.erase(start, it);
                    mp[q[0]] = q[1];
                }
            }

            /*
