class Solution {
    static const int N = 1e5 + 10;
    struct node {
        int l, r, s, f;
    } tr[N << 2];

    inline void pushup(int u) {
        tr[u].s = tr[u << 1].s + tr[u << 1 | 1].s;
    }

    inline void pushdown(int u) {
        if (!tr[u].f) return;
        tr[u].f = 0;
        tr[u << 1].s = tr[u << 1 | 1].s = 0;
        tr[u << 1].f = tr[u << 1 | 1].f = 1;
    }

    inline void build(int u, int l, int r) {
        tr[u].l = l, tr[u].r = r;
        if (l == r) {
            tr[u].s = 1;
            return;
        }
        int mid = (l + r) >> 1;
        build(u << 1, l, mid);
        build(u << 1 | 1, mid + 1, r);
        pushup(u);
    }

    inline void modify(int u, int l, int r) {
        if (tr[u].l >= l && tr[u].r <= r) {
            tr[u].s = 0, tr[u].f = 1;
            return;
        }
        pushdown(u);
        int mid = (tr[u].l + tr[u].r) >> 1;
        if (mid >= l) {
            modify(u << 1, l, r);
        }
        if (mid + 1 <= r) {
            modify(u << 1 | 1, l, r);
        }
        pushup(u);
    }

public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        build(1, 0, n - 1);
        vector<int> ans;
        for (int i = 0; i < queries.size(); i++) {
            int x = queries[i][0], y = queries[i][1];
            modify(1, x + 1, y - 1);
            ans.push_back(tr[1].s - 1);
        }
        return ans;
    }
};
