class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        set <int> adj;
        for (int i = 0; i < n; ++i)
            adj.insert (i);
        for (const auto& q : queries)
        {
            int u = q[0], v = q[1];
            auto s_it = adj.upper_bound (u);
            auto e_it = adj.lower_bound (v);
            if (s_it != e_it)
                adj.erase (s_it, e_it);
            // for (int i : adj)
            //     cout << i << " ";
            // cout << "\n";
            ans.push_back (adj.size () - 1);
        }
        return ans;
    }


};
