import java.util.ArrayList;
import java.util.Arrays;
import java.util.TreeMap;
import java.util.TreeSet;

class Solution {

    ArrayList<Integer>[] al = new ArrayList[500];
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        int ql = queries.length;
        int[] arr1 = new int[ql];
        Node hd = new Node(0, null);
        Node cur = hd;
        TreeMap<Integer, Node> tm = new TreeMap<>();
        tm.put(0, hd);
        for(int i=1;i<n;i++) {
            Node nd = new Node(i, null);
            cur.next = nd;
            cur = nd;
            tm.put(i, nd);
        }

        int dist = n-1;
        for(int i=0;i<ql;i++) {
            int src = queries[i][0];
            int dest = queries[i][1];

            // System.out.println(src+" "+dest);

            if(tm.containsKey(src) && tm.containsKey(dest)) {
                var nd = tm.get(src);
                var curNd = nd;
                var ndDest = tm.get(dest);
                var next = nd.next;
                while(curNd.val != dest) {
                    curNd = curNd.next;
                    if(curNd != null && curNd.val != dest) {
                        dist--;
                        tm.remove(curNd.val);
                    }
                    else break;
                }

                nd.next = ndDest;
            }

            // System.out.println(i);
            // var cc = tm.get(0);
            // while (cc != null) {
            //     System.out.println(cc.val+" => ");
            //     cc = cc.next;
            // }
            arr1[i] = dist;
        }

        return arr1;
    }
}

class Node {
    public int val;
    public Node next;

    public Node(int val, Node next) {
        this.val = val;
        this.next = next;
    }
}
