class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
    vector<int>ans;
        map<int,int>mp;
        for(int i=0;i<n-1;i++)
        {
            mp[i]=i+1;
        }
        int cnt=n-1;
        for(auto i : queries)
        {
            vector<int>v1;
            if(mp.find(i[0])==mp.end()||mp.find(i[1])==mp.end()&&i[1]<(n-1))
            {    ans.push_back(cnt);
            continue;
           }
            for(auto it=mp.lower_bound(i[0]);it!=mp.end();it++)
            {
              //  cout<<(*it).first<<" ";
                if(i[1]>(*it).first&&i[0]<=(*it).first)
                {
                v1.push_back((*it).first);
                cnt--;
                }
                else
                {
                    break;
                }
                cout<<"OP";
            }
            for(auto j : v1)
            {
                if(j>i[0])
                mp.erase(j);
            }
            cnt++;
            //cout<<endl;
            mp[i[0]]=i[1];
            ans.push_back(cnt);
        }
        return(ans);
    }
};
