class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        deleted = set()
        graph = {}
        for i in range(n - 1):
            graph[i] = i + 1
        result = []
        for u, v in queries:
            if u in deleted or v in deleted:
                result.append(n - len(deleted) - 1)
                continue
            s = graph[u]
            while s != v:
                deleted.add(s)
                s = graph[s]
            graph[u] = v
            result.append(n - len(deleted) - 1)
        return result
