class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> d(n, 0);
        for (int i = 1; i < n; ++i)
        {
            d[i] = d[i - 1] + 1;
        }

        set<int> here;
        for (int i = 0; i < n; ++i)
        {
            here.insert(i);
        }

        vector<int> r;

        for (const auto &qi : q)
        {
            int l = qi[0];
            int rrtcamhere = qi[1];

            auto lbound = here.lower_bound(l + 1);
            auto ubound = here.lower_bound(rrtcamhere);

            here.erase(lbound, ubound);

            r.push_back(here.size() - 1);
        }

        return r;
    }
};
