class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int,int> mp;
        vector<int> res;
        int dis=n-1;
        for(auto& p:queries){
            int &u=p[0],&v=p[1];
            auto r=mp.lower_bound(u),l=mp.upper_bound(u);
            vector<int> d;
            if(l!=mp.begin()){
                l--;
                if(l->second>=v)u=v=-1;
            }
            if(u!=v)for(;r!=mp.end();r++){
                if(r->second>v)break;
                dis+=r->second-r->first-1;
                d.emplace_back(r->first);
            }

            for(auto& idx:d)mp.erase(idx);
            if(u!=-1){
                mp[u]=v;
                dis-=v-u-1;
            }
            res.emplace_back(dis);
        }
        return res;
    }
};
