class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        ans.reserve(queries.size());
        set<pair<int, int>> st;
        for(int i = 0 ; i < n - 1; ++i)
        {
            st.insert({i, i + 1});
        }

        for(auto cur : queries)
        {
            int u = cur[0];
            int v = cur[1];

            int l = u, r = v;
            bool removed = false;
            while(true)
            {
                auto it = st.lower_bound({l, l});
                if(it == st.end() or it->first >= r)
                {
                    break;
                }
                removed = true;
                st.erase(it);
            }

            if(removed) st.insert({l, r});
            ans.push_back(st.size());
        }   

        return ans;
    }
};
