class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> result;

        vector<int> dp(n, 0);
        for(int i=1; i< n;i++){
            dp[i] = dp[i-1] + 1;
        }

        set<int> st;
        for(int i = 0; i<n;i++){
            st.insert(i);
        }


        for(auto &it : queries){
            int u = it[0];
            int v = it[1];

            auto lb = st.lower_bound(u+1);
            auto rb = st.lower_bound(v);

            st.erase(lb, rb);
            result.push_back(st.size()-1);
        }
        return result;
    }
};
