class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i = 0; i < n; i ++) {
            s.insert(i);
        }
        int q = queries.size();
        vector<int> ans(q);
        for (int i = 0; i < q; i ++) {
            int u = queries[i][0];
            int v = queries[i][1];
            auto it = s.upper_bound(u);
            vector<int> x;
            while (it != s.end() && *it < v) {
                x.push_back(*it);
                it ++;
            }
            for (int t: x) {
                s.erase(t);
            }
            ans[i] = s.size() - 1;
        }
        return ans;
    }
};
