class DSU {
public:
    vector<int> par, rank;
    vector<vector<int>> adj;
    int n;

    DSU(int size) : n(size) {
        par.resize(size);
        rank.resize(size, 0);
        adj.resize(size);

        for (int i = 1; i < size; ++i) {
            par[i] = i-1;
        }
        for (int i = 0; i < size - 1; ++i) {
            adj[i].push_back(i + 1);
        }
    }

    int find(int u) {
        if (par[u] == u) {
            return u;
        }
        return par[u] = find(par[u]);
    }

    void unionByRank(int u, int v) {
        int ult_u = find(u);
        int ult_v = find(v);
        if (ult_u == ult_v) {
            return;
        }

        if (rank[ult_u] > rank[ult_v]) {
            par[ult_v] = ult_u;
        } 
        else if (rank[ult_u] < rank[ult_v]) {
            par[ult_u] = ult_v;
        } 
        else {
            par[ult_v] = ult_u;
            rank[ult_u]++;
        }
    }

    vector<int> bfs(int src) {
        vector<int> dist(n, INT_MAX);
        queue<int> q;
        dist[src] = 0;
        q.push(src);

        while (!q.empty()) {
            int u = q.front();
            q.pop();

            for (int v : adj[u]) {
                if (dist[v] == INT_MAX) {
                    dist[v] = dist[u] + 1;
                    q.push(v);
                }
            }
        }
        return dist;
    }
};

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dist(n,0);

        for(int i = 1; i < n; i++){
            dist[i] = dist[i-1] + 1;
        }

        set<int> st;
        for(int i = 0; i < n; i++){
            st.insert(i);
        }

        vector<int> ans;

        for(auto& it: queries){
            int u = it[0];
            int v = it[1];

            auto ind1 = st.lower_bound(u + 1);
            auto ind2 = st.lower_bound(v);

            st.erase(ind1,ind2);

            int temp = st.size();
            ans.push_back(temp - 1);
        }

        return ans;
    }
};
