class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i = 0; i < n; i ++) s.insert(i);
        vector<int> res;

        for(auto& v: queries)
        {
            if(s.find(v[0]) == s.end()){
                res.push_back(s.size()-1);
                continue;
            }
            auto it0 = s.lower_bound(v[0]);it0++;
            auto it1 = s.lower_bound(v[1]);
            if(it0!=s.end())
            s.erase(it0,  it1);
            res.push_back(s.size()-1);
            // for(int i: s)cout<<i<<" ";
            // cout<<endl;
        }

        return res;
    }
};
