class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> lol(n, 0);
        for (int i = 1; i < n; i=i+1)
        lol[i] = lol[i - 1] + 1;

        set<int> p;
        for (int i = 0; i < n; i=i+1)
        p.insert(i);

        vector<int> x;

        for (const auto& i : q) {

            auto lower_bound = p.lower_bound(i[0] + 1);
            auto upper_bound = p.lower_bound(i[1]);

            p.erase(lower_bound, upper_bound);

            x.push_back(p.size() - 1);
        }

        return x;
    }
};
