class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> adj(n, 0);
        vector<int> ans;
        set<int> s;

        for (int i = 1; i < n; i++) {
            adj[i] = adj[i - 1] + 1;
        }

        for (int i = 0; i < n; i++) {
            s.insert(i);
        }

        for (auto it : queries) {
            auto lb = s.lower_bound(it[0] + 1);
            auto ub = s.lower_bound(it[1]);
            s.erase(lb, ub);
            ans.push_back(s.size() - 1);
        }

        return ans;
    }
};
