class Solution {
public:

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        vector<int> ans;
        int val = n-1;
        set<int> ls;
        for(int i =0;i<n;i++)ls.insert(i);//O(n)
        for(int i =0;i<queries.size();i++){
            int  l = queries[i][0] , r=queries[i][1];
            auto start = ls.upper_bound(l); //O(log(size of set))
            while(start!=ls.end() && *start<r ){//O(log(size of set))
                ls.erase(start),start=ls.upper_bound(l);
            }
            ans.push_back(ls.size()-1);
        }
        return ans;

    }

};
