from sortedcontainers import SortedList


class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        cities = SortedList([i for i in range(n)])
        answer = []
        for u, v in queries:
            p = cities.bisect_left(u + 1)
            while p >= 0 and p < len(cities) and cities[p] > u and cities[p] < v:
                cities.pop(p)
            answer.append(len(cities) - 1)
        return answer
