int mod = 1e9 + 7;

int mod_mul(long long a, int b) { return a * b % mod; }

int mod_pow(int b, int p) {
    if (!p)
        return 1;
    int half = mod_pow(b, p / 2);
    return mod_mul(mod_mul(half, half), p % 2 ? b : 1);
}

int mod_inv(int b) { return mod_pow(b, mod - 2); }

int mod_factorial(int x) {
    if (x < 2)
        return 1;
    return mod_mul(x, mod_factorial(x - 1));
}
