        map<int, int> m;
        int d = n - 1;
        for (auto &q : qs) {
            int u = q[0], v = q[1];
            // _p(pair{u,v});
            // _p("A \n\n");
            auto it = m.upper_bound(u);
            // _p(prev(it)->second);
