                // _p(prev(it)->second);
                ans.push_back(d);
                continue;
            }
            vector<int>dels;
            for (auto b = m.lower_bound(u), e = m.lower_bound(v); b != e; ++b) {
                // _p(tuple{"d",b->first,b->second});
                d += b->second - b->first - 1;
                dels.push_back(b->first);
                // m.erase(b);
            }
            for(auto de:dels)
            m.erase(de);
            // for (auto b = m.lower_bound(u), e = m.lower_bound(v); b != e; ++b) {
