class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> d(n, 0);
        for (int i = 1; i < n; ++i) {
            d[i] = d[i - 1] + 1;
        }

        set<int> pt;
        for (int i = 0; i < n; ++i) {
            pt.insert(i);
        }

        vector<int> rp;

        for (const auto& qi : q) {
            int l = qi[0];
            int rt = qi[1];

            auto lb = pt.lower_bound(l + 1);
            auto ub = pt.lower_bound(rt);

            pt.erase(lb, ub);

            rp.push_back(pt.size() - 1);
        }

        return rp;
    }
};
