class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>dist(n,0);
        for(int i=1;i<=n-1;i++)
        {
            dist[i]=dist[i-1]+1;
        }

        set<int>pos;
        multiset<int>st;
        set<int>lai;
        for(int i=0;i<n;i++)
        {
            pos.insert(i);
            st.insert(i);
            st.insert(i+1);
            st.insert(i-1);
        }
        vector<int>ans;
        for(int i=1;i<n/2;i++)
        {
            st.erase(i);
        }


        for(auto g: queries)
        {
            int fro = g[0];
            int to = g[1];

            st.erase(fro);
            st.erase(to);

            auto it1=pos.lower_bound(fro+1);
            auto it2=pos.lower_bound(to);

            lai.insert(fro);
            lai.insert(to);


            pos.erase(it1,it2);
            ans.push_back(pos.size()-1);
        }

        for(int i=0;i<n;i++)
        {
            lai.erase(i);
            st.erase(i);
        }
        return ans;

    }
};
