#include <vector>
#include <set>

class Solution {
public:
    std::vector<int> shortestDistanceAfterQueries(int totalElements, std::vector<std::vector<int>>& queries) {
        std::set<int> activeElements;

        for (int i = 0; i < totalElements; i++) {
            activeElements.insert(i);
        }

        int remainingElements = totalElements - 1;
        std::vector<int> results;

        for (int i = 0; i < queries.size(); i++) {
            int startRange = queries[i][0];
            int endRange = queries[i][1];
            std::vector<int> elementsToRemove;

            auto it = activeElements.upper_bound(startRange);
            int count = 0;
            while (it != activeElements.end() && *it < endRange) {
                elementsToRemove.push_back(*it);
                ++it;
            }

            for (int j = 0; j < elementsToRemove.size(); j++) {
                if (elementsToRemove[j] > startRange && elementsToRemove[j] < endRange) {
                    count++;
                    activeElements.erase(elementsToRemove[j]);
                }
            }

            remainingElements -= count;
            results.push_back(remainingElements);
        }

        return results;
    }
};

