class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        ios::sync_with_stdio(false);
        cin.tie(nullptr);
        vector<int>ans;
        set<int>s;
        for(int i=0;i<n;++i)s.insert(i);
        for(auto i:q){
            auto l=s.lower_bound(i[0]+1);
            while(l!=s.end() && *l<i[1]){
                l=s.erase(l);
            }
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
