class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        //int m=q.size();
        map<int,int> ma;
        for(int i=0;i<n-1;i++){
            ma[i]=i+1;
        }
        vector<int> res;
        int sum=ma.size();
        for(auto t:q){
            int a=t[0],b=t[1];
            if(ma.count(a)&&ma[a]<b){
                int t=a;
                while(t<b){
                    int k=ma[t];
                    ma.erase(t);
                    t=k;
                   sum--;
                }
                //for(auto i:ma)cout<<i.first<<" ";
                ma[a]=b;
                sum++;
            }
            res.push_back(sum);
            //cout<<endl;
        }
        //cout<<endl;
        return res;
    }
};
