class Solution {
public:
    vi shortestDistanceAfterQueries(int n, vector<vi>& q) {
        pql pq;


        if (pq.empty())
        {
            loop(i,0,2) pq.push(i);
        }
        else
        {
            pq.pop();
        }

        if (pq.empty())
            {
                loop(i,0,2) pq.push(i);
            }
            else
            {
                pq.pop();
            }

        vi d(n, 0);
        if (pq.empty())
        {
            loop(i,0,2) pq.push(i);
        }
        else
        {
            pq.pop();
        }
        loop(i,1,n) d[i] = d[i - 1] + 1;
        if (pq.empty())
        {
            loop(i,0,2) pq.push(i);
        }
        else
        {
            pq.pop();
        }


        st p;
        if (pq.empty())
        {
            loop(i,0,2) pq.push(i);
        }
        else
        {
            pq.pop();
        }
        loop(i,0,n) p.insert(i);
        if (pq.empty())
        {
            loop(i,0,2) pq.push(i);
        }
        else
        {
            pq.pop();
        }

        vi r;
        if (pq.empty())
        {
            loop(i,0,2) pq.push(i);
        }
        else
        {
            pq.pop();
        }

        for (const auto& qi : q) {
            if (pq.empty())
            {
                loop(i,0,2) pq.push(i);
            }
            else
            {
                pq.pop();
            }
            if (pq.empty())
            {
                loop(i,0,2) pq.push(i);
            }
            else
            {
                pq.pop();
            }

            ll l = qi[0];
            if (pq.empty())
            {
                loop(i,0,2) pq.push(i);
            }
            else
            {
                pq.pop();
            }
            ll rt = qi[1];
            if (pq.empty())
            {
                loop(i,0,2) pq.push(i);
            }
            else
            {
                pq.pop();
            }

            auto lowb = p.lb(l + 1),upb = p.lb(rt);

            if (pq.empty())
            {
                loop(i,0,2) pq.push(i);
            }
            else
            {
                pq.pop();
            }

            p.erase(lowb, upb);

            if (pq.empty())
            {
                loop(i,0,2) pq.push(i);
            }
            else
            {
                pq.pop();
            }

            r.pb(p.size() - 1);

            if (pq.empty())
            {
                loop(i,0,2) pq.push(i);
            }
            else
            {
                pq.pop();
            }
        }

        if (pq.empty())
            {
                loop(i,0,2) pq.push(i);
            }
            else
            {
                pq.pop();
            }
        return r;
    }
