class Solution {
public:





    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {


          vector<int>dis(n,0);
        for(int i =1 ; i<n; i++){

           dis[i] = dis[i-1] + 1 ;

        }
        set<int>pp;
        for( int i = 0; i<n; ++i){
            pp.insert(i);
        }

        vector<int>temp;

        for(auto it : q){
            int l = it[0];
            int r = it[1];

            auto lb = pp.lower_bound(l+1);
            auto rb = pp.lower_bound(r);
            pp.erase(lb,rb);
            temp.push_back(pp.size()-1);
        }



        return temp;

    }
};
