class Solution {

public:

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> v(n, 0);
        for (int i=1; i<n; ++i) {
            v[i]=v[i-1]+1;
        }
        set<int> st;
        for (int i=0; i<n; ++i) {
            st.insert(i);
        }

        vector<int> ans;
        for (const auto& it : q) {
            int left = it[0];
            int right = it[1];
            auto lob = st.lower_bound(left + 1);
            auto uob = st.lower_bound(right);
            st.erase(lob,uob);
            ans.push_back(st.size()-1);
        }

        return ans;
    }
};
