class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i=0;i<=n-1;i++){
            s.insert(i);
        }
        vector<int> ans;

        for(auto it:queries){
            int l=it[0];
            int r=it[1];
            int i;
            // i=l+1;

            while(1){
                auto it=s.upper_bound(l);
                if(*(it)<r){
                    s.erase(s.find(*(it)));
                }
                else {
                    break;
                }
            }

            ans.push_back(s.size()-1);
        }

        return ans;
    }
};
