class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        TreeSet<Integer> ts = new TreeSet<>();
        for(int i=0;i<n;i++) ts.add(i);
        int m = queries.length;
        int[] res = new int[m];
        for(int i=0;i<m;i++) {
            int[] q = queries[i];
            while(true) {
                Integer p = ts.higher(q[0]);
                if(p==null || p>=q[1]) break;
                ts.remove(p);
            }
            res[i] = ts.size()-1;
        }
        return res;
    }
}
