class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int,int> m;
        for(int i=n-2;i>=0;i--) m[i]=i+1;
        int v = n-1;
        vector<int> r;
        for(auto q : queries)
        {
            auto it = prev(m.upper_bound(q[0]));
            if(it->first>=q[0] && it->second<=q[1])
            {
                while(it!=m.end() && it->first>=q[0] && it->second<=q[1])
                {
                    v--;
                    m.erase(it++);
                }
                m[q[0]]=q[1];
                v++;
            }
            //for(auto [k,v] : m) cout<<k<<" "<<v<<endl;
            //cout<<endl;
            //cout<<v<<endl;
            r.push_back(v);
        }
        return r;
    }
};
