class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& que) {
        vector<int> dis(n,0);
        for(int i=1;i<n;i++) {
            dis[i]=i;
        }

        set<int> st;
        for(int i=0;i<n;i++) {
            st.insert(i);
        }

        vector<int> ans;

        for(auto it:que) {
            int u=it[0],v=it[1];

            auto lob = st.lower_bound(u+1);
            auto uob = st.lower_bound(v);

            st.erase(lob,uob);

            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
