class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for(int i=0;i<n;i++) st.insert(i);
        int m=queries.size();
        vector<int> res;
        for(auto e:queries){
            int s=e[0],ed=e[1];
            while(1){
                auto it=st.upper_bound(s);
                if(it==st.end()) break;
                if(*it>=ed) break;
                st.erase(*it);
            }
            res.push_back((int)(st.size())-1);
        }return res;
    }
};
