class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> res;
        set<int> s;
        for (int i = 0; i < n; ++i) {
            s.insert(i);
        }

        for (const auto& x : q) {
            int l = x[0];
            int r = x[1];

            auto lb = s.lower_bound(l + 1);
            auto ub = s.lower_bound(r);

            vector<int> to_remove;
            for (auto it = lb; it != ub; ++it) {
                to_remove.push_back(*it);
            }
            for (int v : to_remove) {
                s.erase(v);
            }

            res.push_back(s.size() - 1);
        }

        return res;
    }
};
