class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> distances(n, 0);
        for (int i = 1; i < n; ++i) {
            distances[i] = distances[i - 1] + 1;
        }

        set<int> s;
        for (int i = 0; i < n; ++i) {
            s.insert(i);
        }

        vector<int> ans;

        for (const auto& it : queries) {
            int start = it[0];
            int end = it[1];

            auto lower_bound = s.lower_bound(start + 1);
            auto upper_bound = s.lower_bound(end);

            s.erase(lower_bound, upper_bound);

            ans.push_back(s.size() - 1);
        }

        return ans;
    }
};
