#include <vector>
#include <set>

using namespace std;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int numElements, vector<vector<int>>& queries) {
        vector<int> distances(numElements, 0);
        for (int i = 1; i < numElements; ++i) {
            distances[i] = distances[i - 1] + 1;
        }

        set<int> elementsSet;
        for (int i = 0; i < numElements; ++i) {
            elementsSet.insert(i);
        }

        vector<int> result;

        for (const auto& query : queries) {
            int leftBound = query[0];
            int rightBound = query[1];

            auto lowerBound = elementsSet.lower_bound(leftBound + 1);
            auto upperBound = elementsSet.lower_bound(rightBound);

            elementsSet.erase(lowerBound, upperBound);

            result.push_back(elementsSet.size() - 1);
        }

        return result;
    }
};
