class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] q) {
        Map<Integer, Integer> map = new HashMap<>();
        for (int i = 0; i < n - 1; i++)
            map.put(i, i + 1);
        int length = q.length;
        int[] res = new int[length];
        for (int i = 0; i < length; i++) {
            int start = q[i][0];
            boolean toAdd = map.containsKey(start);
            while (map.containsKey(start) && map.get(start) <= q[i][1]) {
                int next = map.get(start);
                // System.out.println(start + " " + next);
                map.remove(start);
                start = next;
            }
            if (toAdd)
                map.put(q[i][0], q[i][1]);
            res[i] = map.size();
        }
        return res;
    }
}
