typedef long long ll;
#define fast_io                  \
    ios::sync_with_stdio(false); \
    cin.tie(nullptr);
#define vi vector<int>
#define For(i, a, b) for (ll i = (a); i < (b); i++)
#define srt(v) sort(v.begin(), v.end());
#define pb push_back
#define si set<int>
#define INF LLONG_MAX
#define lb(a) lower_bound(a)
using namespace std;

class Solution
{
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>> &queries)
    {
        vi v(n, 0), res;
        si st;
        For(i, 1, n)
        {
            v[i] = v[i - 1] + 1;
        }
        For(i, 0, n)
        {
            st.insert(i);
        }

        for (auto &q : queries)
        {
            int l = q[0], r = q[1];
            auto up = st.lb(r);
            auto low = st.lb(l + 1);

            st.erase(low, up);
            res.pb(st.size() - 1);
        }

        return res;
    }
};
